import React, { useState, useEffect } from 'react'

import bookService from '../services/book'

const Form = ({ currentBook, setCurrentBook, books, setBooks }) => {
    const [title, setTitle] = useState(currentBook.title)
    const [author, setAuthor] = useState(currentBook.author)
    const [description, setDescription] = useState(currentBook.description)
    const [isDisabled, setIsDisabled] = useState(true)

    useEffect(() => {
        setTitle(currentBook.title)
        setAuthor(currentBook.author)
        setDescription(currentBook.description)

        // Only change disabled state after a currentbook is set by user
        if (currentBook.id !== null) {
            setIsDisabled(false)
        } else {
            setIsDisabled(true)
        }
    }, [currentBook])

    const titleInputHandler = (e) => {
        setTitle(e.target.value)
    }

    const authorInputHandler = (e) => {
        setAuthor(e.target.value)
    }

    const descriptionInputHandler = (e) => {
        setDescription(e.target.value)
    }

    const submitHandler = async (e) => {
        e.preventDefault()
        const newBook = {
            title: title,
            author: author,
            description: description,
        }
        try {
            await bookService.addBook(newBook)
            const newBooks = books.push(newBook)
            setBooks(newBooks)
        } catch (error) {
            console.log('Error: addBook')
            console.log(error)
        }
    }

    const updateHandler = async (e) => {
        e.preventDefault()
        const updatedBook = {
            ...currentBook,
            title: title,
            author: author,
            description: description,
        }
        try {
            await bookService.updateBook(currentBook.id, updatedBook)
            const newBooks = books.map((b) =>
                b.id === updatedBook.id ? updatedBook : b
            )
            setBooks(newBooks)
        } catch (error) {
            console.log('Error: updateBook')
            console.log(error)
        }
    }

    const deleteHandler = async (e) => {
        e.preventDefault()
        try {
            await bookService.deleteBook(currentBook.id)
            const newBooks = books.map((b) => b.id !== currentBook.id)
            setBooks(newBooks)
        } catch (error) {
            console.log('Error: deleteBook')
            console.log(error)
        }
    }

    const clearSelectionHandler = (e) => {
        e.preventDefault()
        const clearedFormData = {
            id: null,
            title: '',
            author: '',
            description: '',
        }
        setCurrentBook(clearedFormData)
    }

    return (
        <div className="form">
            <form>
                <label>Title</label>
                <div>
                    <input
                        className="form-input"
                        type="text"
                        name="title"
                        value={title}
                        onChange={titleInputHandler}
                    />
                </div>
                <label>Author</label>
                <input
                    className="form-input"
                    type="text"
                    name="author"
                    value={author}
                    onChange={authorInputHandler}
                />
                <label>Description</label>
                <textarea
                    rows="3"
                    className="form-textarea"
                    name="description"
                    value={description}
                    onChange={descriptionInputHandler}
                />
                <div className="form-buttons">
                    <button className="button" onClick={submitHandler}>
                        Save New
                    </button>
                    <button
                        className="button"
                        disabled={isDisabled}
                        onClick={updateHandler}
                    >
                        Save
                    </button>
                    <button
                        className="button"
                        disabled={isDisabled}
                        onClick={deleteHandler}
                    >
                        Delete
                    </button>
                    <button
                        className="button"
                        disabled={isDisabled}
                        onClick={clearSelectionHandler}
                    >
                        Clear book selection
                    </button>
                </div>
            </form>
        </div>
    )
}

export default Form
