import React from 'react'

import ListItem from './ListItem'

const List = ({ setCurrentBook, bookList }) => {
    const clearSelectionHandler = () => {
        const clearedFormData = {
            id: null,
            title: '',
            author: '',
            description: '',
        }
        setCurrentBook(clearedFormData)
    }
    return (
        <div className="list">
            <ul>
                {bookList.map((b) => (
                    <ListItem
                        key={b.title}
                        book={b}
                        setCurrentBook={setCurrentBook}
                    />
                ))}
            </ul>
            <button onClick={clearSelectionHandler}>clear selection</button>
        </div>
    )
}

export default List
