import React from 'react'

const ListItem = ({ book, setBook }) => {

    const clickHandler = () => {
        setBook(book)
    }

    return (
        <li key={book.title}>
            <div className='list-item' onClick={clickHandler}>
                {book.title} {book.author}
            </div>
        </li>
    )
}

export default ListItem
