import React, { useState } from 'react'

const Form = () => {
    const [title, setTitle] = useState()
    const [author, setAuthor] = useState()
    const [description, setDescription] = useState()

    const titleInputHandler = (e) => {
        setTitle(e.target.value)
    }

    const authorInputHandler = (e) => {
        setAuthor(e.target.value)
    }

    const descriptionInputHandler = (e) => {
        setDescription(e.target.value)
    }

    const submitHandler = (e) => {
        e.preventDefault()
        console.log(title, author, description)
    }

    return (
        <form onSubmit={submitHandler}>
            <label>
                Title
                <input type='text' name='title' value={title} onChange={titleInputHandler} />
            </label>
            <label>
                Author
                <input type='text' name='author' value={author} onChange={authorInputHandler} />
            </label>
            <label>
                Description
                <input type='text' name='description' value={description} onChange={descriptionInputHandler} />
            </label>
            <input type='submit' value='Save' />
        </form>
    )
}

export default Form
