    //             console.log(error)
    //         }
    //     }
    //     getBooks()
    // }, [])

    return (
        <div className="app">
            <Form
                setSelectedBook={setSelectedBook}
                currentBook={currentBook}
                setCurrentBook={setCurrentBook}
                books={books}
                setBooks={setBooks}
            />
            <List
                selectedBook={selectedBook}
                setSelectedBook={setSelectedBook}
                setCurrentBook={setCurrentBook}
                bookList={books}
            />
        </div>
    )
}

export default App
