    {
        title: 'Howdy1',
        author: 'Matt',
        description: 'He is just the best',
    },
    {
        title: 'Howdy2',
        author: 'Matt',
        description: 'He is just the best',
    },
    {
        title: 'Howdy3',
        author: 'Matt',
        description: 'He is just the best',
    },
    {
        title: 'Howdy4',
        author: 'Matt',
        description: 'He is just the best',
    },
]

const App = () => {
    const [currentBook, setCurrentBook] = useState('')
    const [books, setBooks] = useState(bookList)

    console.log(currentBook)
    return (
        <div className="app">
            <Form book={currentBook} />
            <List setCurrentBook={setCurrentBook} bookList={books} />
        </div>
    )
}

export default App
