import React, { useState } from 'react'
import ListItem from './ListItem'

const List = ({ setBook }) => {
    const [books, setBooks] = useState(bookList)

    return (
        <div className="list">
            <ul>
                {books.map((b) => (
                    <ListItem book={b} setBook={setBook} />
                ))}
            </ul>
        </div>
    )
}

export default List
