import React, { useState, useEffect } from 'react'

import bookService from '../services/book'

const Form = ({ currentBook, books, setBooks }) => {
    const [title, setTitle] = useState(currentBook.title)
    const [author, setAuthor] = useState(currentBook.author)
    const [description, setDescription] = useState(currentBook.description)
    const [isDisabled, setIsDisabled] = useState(true)

    useEffect(() => {
        setTitle(currentBook.title)
        setAuthor(currentBook.author)
        setDescription(currentBook.description)

        // Only change disabled state after a currentbook is set by user
        if (currentBook.id !== null) {
            setIsDisabled(false)
        }
    }, [currentBook])

    const titleInputHandler = (e) => {
        setTitle(e.target.value)
    }

    const authorInputHandler = (e) => {
        setAuthor(e.target.value)
    }

    const descriptionInputHandler = (e) => {
        setDescription(e.target.value)
    }

    const submitHandler = async (e) => {
        e.preventDefault()
        const newBook = {
            title: title,
            author: author,
            description: description,
        }

        try {
            const res = await bookService.addBook(newBook)
            const newBooks = books.push(newBook)
            setBooks(newBooks)
        } catch (error) {
            console.log(error)
        }
    }

    const updateHandler = async () => {
        const updatedBook = {
            ...currentBook,
            title: title,
            author: author,
            description: description,
        }
        try {
            const res = await bookService.updateBook(
                currentBook.id,
                updatedBook
            )
            const newBooks = books.map((b) =>
                b.id === updatedBook.id ? updatedBook : b
            )
            setBooks(newBooks)
        } catch (error) {
            console.log(error)
        }
    }

    const deleteHandler = async () => {
        try {
            await bookService.deleteBook(currentBook.id)
            const newBooks = books.map((b) => b.id !== currentBook.id)
            setBooks(newBooks)
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <div className="form">
            <form onSubmit={submitHandler}>
                <label>Title</label>
                <input
                    type="text"
                    name="title"
                    value={title}
                    onChange={titleInputHandler}
                />
                <label>Author</label>
                <input
                    type="text"
                    name="author"
                    value={author}
                    onChange={authorInputHandler}
                />
                <label>Description</label>
                <textarea
                    value={description}
                    onChange={descriptionInputHandler}
                />
                <div className="form-buttons">
                    <input type="submit" value="Save New" />
                    <button disabled={isDisabled} onClick={updateHandler}>
                        Save
                    </button>
                    <button disabled={isDisabled} onClick={deleteHandler}>
                        Delete
                    </button>
                </div>
            </form>
        </div>
    )
}

export default Form
