import React from 'react'

const ListItem = ({ book, setCurrentBook }) => {
    const clickHandler = () => {
        setCurrentBook(book)
    }

    return (
        <li key={book.title}>
            <div className="list-item" onClick={clickHandler}>
                Title: {book.title} Author: {book.author}
            </div>
        </li>
    )
}

export default ListItem
