import React, { useState, useEffect } from 'react'

import bookService from '../services/book'

const clearedFormData = {
    id: null,
    title: null,
    author: null,
    description: null,
}

const Form = ({
    setSelectedBook,
    currentBook,
    setCurrentBook,
    books,
    setBooks,
}) => {
    const [title, setTitle] = useState(currentBook.title)
    const [author, setAuthor] = useState(currentBook.author)
    const [description, setDescription] = useState(currentBook.description)
    const [isDisabled, setIsDisabled] = useState(true)

    useEffect(() => {
        setTitle(currentBook.title)
        setAuthor(currentBook.author)
        setDescription(currentBook.description)

        // Only change buttons disabled state after a currentbook is set by user
        currentBook.id ? setIsDisabled(false) : setIsDisabled(true)
    }, [currentBook])

    const titleInputHandler = (e) => {
        setTitle(e.target.value)
    }

    const authorInputHandler = (e) => {
        setAuthor(e.target.value)
    }

    const descriptionInputHandler = (e) => {
        setDescription(e.target.value)
    }

    const submitHandler = async (e) => {
        e.preventDefault()
        const newBook = {
            title: title,
            author: author,
            description: description,
        }
        try {
            const { data } = await bookService.addBook(newBook)
            const newBooks = [...books, data]
            setBooks(newBooks)
            setCurrentBook(clearedFormData)
        } catch (error) {
            console.log('Error: addBook')
            console.log(error)
        }
    }

    const updateHandler = async (e) => {
        e.preventDefault()
        const updatedBook = {
            ...currentBook,
            title: title,
            author: author,
            description: description,
        }
        try {
            await bookService.updateBook(currentBook.id, updatedBook)
            const newBooks = books.map((b) =>
                b.id === updatedBook.id ? updatedBook : b
            )
            setBooks(newBooks)
            setCurrentBook(clearedFormData)
        } catch (error) {
            console.log('Error: updateBook')
            console.log(error)
        }
    }

    const deleteHandler = async (e) => {
        e.preventDefault()
        try {
            await bookService.deleteBook(currentBook.id)
            const newBooks = books.filter((b) => b.id !== currentBook.id)
            setBooks(newBooks)
        } catch (error) {
            console.log('Error: deleteBook')
            console.log(error)
        }
        setCurrentBook(clearedFormData)
    }

    const clearSelectionHandler = (e) => {
        e.preventDefault()
        const clearedFormData = {
            id: null,
            title: null,
            author: null,
            description: null,
        }
        setCurrentBook(clearedFormData)
        setSelectedBook(null)
    }

    return (
        <div className="form">
            <h3>Book Form: </h3>
            <form>
                <label>Title</label>
                <div>
                    <input
                        className="form-input"
                        type="text"
                        name="title"
                        value={title ? title : ''}
                        onChange={titleInputHandler}
                    />
                </div>
                <label>Author</label>
                <div>
                    <input
                        className="form-input"
                        type="text"
                        name="author"
                        value={author ? author : ''}
                        onChange={authorInputHandler}
                    />
                </div>
                <label>Description</label>
                <div>
                    <textarea
                        rows="3"
                        className="form-textarea"
                        name="description"
                        value={description ? description : ''}
                        onChange={descriptionInputHandler}
                    />
                </div>
                <div className="form-buttons">
                    <button
                        className="button"
                        disabled={!isDisabled}
                        onClick={submitHandler}
                    >
                        Save New
                    </button>
                    <button
                        className="button"
                        disabled={isDisabled}
                        onClick={updateHandler}
                    >
                        Save
                    </button>
                    <button
                        className="button"
                        disabled={isDisabled}
                        onClick={deleteHandler}
                    >
                        Delete
                    </button>
                    <button
                        className="button"
                        disabled={isDisabled}
                        onClick={clearSelectionHandler}
                    >
                        Clear book selection
                    </button>
                </div>
            </form>
        </div>
    )
}

export default Form
