import React, { useState } from 'react'
import ListItem from './ListItem'

const bookList = [
    {
        title: 'Howdy1',
        author: 'Matt',
        description: 'He is just the best',
    },
    {
        title: 'Howdy2',
        author: 'Matt',
        description: 'He is just the best',
    },
    {
        title: 'Howdy3',
        author: 'Matt',
        description: 'He is just the best',
    },
    {
        title: 'Howdy4',
        author: 'Matt',
        description: 'He is just the best',
    },
]

const List = () => {
    const [books, setBooks] = useState(bookList)

    return (
        <div className="list">
            <ul>
                {books.map((b) => (
                    <ListItem book={b} />
                ))}
            </ul>
        </div>
    )
}

export default List
