import React, { useState, useEffect } from 'react'

const Form = ({ currentBook }) => {
    console.log(currentBook)
    const [title, setTitle] = useState(currentBook.title)
    const [author, setAuthor] = useState(currentBook.author)
    const [description, setDescription] = useState(currentBook.description)

    useEffect(() => {
        setTitle(currentBook.title)
        setAuthor(currentBook.author)
        setDescription(currentBook.description)
    }, [currentBook])

    const titleInputHandler = (e) => {
        setTitle(e.target.value)
    }

    const authorInputHandler = (e) => {
        setAuthor(e.target.value)
    }

    const descriptionInputHandler = (e) => {
        setDescription(e.target.value)
    }

    const submitHandler = (e) => {
        e.preventDefault()
        console.log(title, author, description)
    }

    const deleteHandler = () => {}

    return (
        <div className="form">
            <form onSubmit={submitHandler}>
                <label>Title</label>
                <input
                    type="text"
                    name="title"
                    value={title}
                    onChange={titleInputHandler}
                />
                <label>Author</label>
                <input
                    type="text"
                    name="author"
                    value={author}
                    onChange={authorInputHandler}
                />
                <label>Description</label>
                <textarea
                    value={description}
                    onChange={descriptionInputHandler}
                />
                <div className="form-buttons">
                    <input type="submit" value="Save New" />
                    <input type="submit" value="Save" />
                    <button onClick={deleteHandler}>Delete</button>
                </div>
            </form>
        </div>
    )
}

export default Form
