import React from 'react'

const ListItem = ({ book, setCurrentBook }) => {
    const clickHandler = () => {
        setCurrentBook(book)
    }

    return (
        <li key={book.title}>
            <div className="list-item" onClick={clickHandler}>
                <p>Title: howdydooooddddddeee eeeee hai <br></br>Author: {book.author}</p>
            </div>
        </li>
    )
}

export default ListItem
