import React, { useState } from 'react'

import ListItem from './ListItem'

const List = ({ setCurrentBook, bookList }) => {
    const [selected, setSelected] = useState(false)
    return (
        <div className="list">
            <h3>Book List: </h3>
            <ul>
                {bookList.map((b) => (
                    <ListItem
                        key={b.title}
                        selected={selected}
                        setSelected={setSelected}
                        book={b}
                        setCurrentBook={setCurrentBook}
                    />
                ))}
            </ul>
        </div>
    )
}

export default List
